// CircularSkillBar.js
import React, { useEffect, useRef } from 'react';

const CircularSkillBar = ({ skillName, percentage }) => {
  const svgRef = useRef(null);

  useEffect(() => {
    const radius = svgRef.current.getAttribute('r');
    const circumference = 2 * Math.PI * radius;
    const progress = (100 - percentage) / 100;
    const dashOffset = circumference * progress;

    svgRef.current.style.strokeDashoffset = dashOffset;
  }, [percentage]);

  return (
    <div className="w-32 h-32 relative">
      <svg className="absolute" width="100%" height="100%">
        <circle
          ref={svgRef}
          r="14"
          cx="50%"
          cy="50%"
          fill="transparent"
          stroke="#3498db"
          strokeWidth="4"
          strokeLinecap="round"
          strokeDasharray="88"
          strokeDashoffset="0"
        />
      </svg>
      <div className="text-center absolute w-full">
        <div className="text-lg font-semibold">{skillName}</div>
        <div className="text-gray-600">{percentage}%</div>
      </div>
    </div>
  );
};

export default CircularSkillBar;
